import { GET_NOTES, DELETE_NOTES } from "../actions/types.js"

const initialState={
    notes: []
}

export default function( state=initialState, action){
    switch (action.type){
        case GET_NOTES:
            return{
                ...state,
                notes: action.payload
            };
        case DELETE_NOTES:
            return{
               ...state,
               notes: state.notes.filter(notes => notes.id !== action.payload) 
            };
        default:
            return state;
    }
}
