import React, { Component } from 'react'

export class Input extends Component {

    state = {
        title: "",
        description: ""
    }

    onChange = (e) => this.setState({ [e.target.title]: e.target.value });

    onAdd = (e) => {
        e.preventDefault();
        console.log("Note added");
    }
    render() {
        const { title, description } = this.state;
        return (
            <div className="card card-body mt-4 mb-4">
                <h2>Add Note</h2>
                <form onSubmit={this.onAdd}>
                    <div class="form-group row">
                        <label for="colFormLabel" class="col-sm-2 col-form-label">Title</label>
                        <div class="col-sm-10">
                            <input type="text" class="form-control" id="colFormLabel" placeholder="Note Title" value={title}></input>
                        </div>
                    </div>
                    <div class="form-group row">
                        <label for="colFormLabelLg" class="col-sm-2 col-form-label col-form-label-lg">Description</label>
                        <div class="col-sm-10">
                            <input type="text" class="form-control form-control-lg" id="colFormLabelLg" placeholder="Note Description" value={description}></input>
                        </div>
                    </div>
                    < div className="form-group" >
                        <button type="submit" className="btn btn-primary">
                            Submit
                        </button>
                    </div >
                </form>
            </div>
        )
    }
}

export default Input
