import axios from "axios";
import { GET_NOTES } from "./types";

export const getNotes = () => dispatch => {
    axios.get("/api/notes/")
    .then( res => {dispatch({
        type: GET_NOTES,
        payload: res.data
    });
    }).catch(
        err=> 
        console.log("something went wrong")
    );
};
